#!/bin/sh
make -f Makefile.emscripten clean; make -f Makefile.emscripten && cd bin/src && $HOME/emsdk/upstream/emscripten/emcc -flto -O2 -fno-rtti -fno-exceptions */*.o */*/*.o main.o -o index.html -sUSE_SDL=2 -sUSE_SDL_MIXER=2 -sSDL2_MIXER_FORMATS='["mp3"]' -sUSE_SDL_IMAGE=2 -sSDL2_IMAGE_FORMATS='["png"]' -sUSE_SDL_TTF=2 -sASYNCIFY -sASYNCIFY_IGNORE_INDIRECT -sASYNCIFY_ONLY=@../../src/plat/web/funcs.txt -sENVIRONMENT=web --preload-file ../../data/@data/ --preload-file ../../fonts/@fonts/ --preload-file ../../gfx/@gfx/ --preload-file ../../music/@music/ --preload-file ../../sound/@sound/ --closure 1 -sEXPORTED_RUNTIME_METHODS=['allocate'] -Wl,-u,fileno && cp ../../src/plat/web/index.html index.html && $HOME/emsdk/upstream/emscripten/emrun index.html
